mapper.select:
select bno, title, content, writer, regdate, readcount, userreco, proreco
from view_b_board
where bno = #{bno}

mapper.update:
update view_b_board
set readcount = readcount + 1
where bno = #{bno}

update view_b_board
set procount = procount + 1
where bno = #{bno}

update view_b_board
set userreco = userreco + 1
where bno = #{bno}

update view_b_board
set proreco = proreco + 1
where bno = #{bno}

update view_b_board
set title = #{title},
content = #{content},
writer = #{writer}
where bno = #{bno}
